mapper.update:
update message
set see= see+1
where id=#{id}

update  message
set statue=1
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, publicid, title, content, topicname, topicid, `type`, photo, statue,public_time,`like`,comments_count,see
from message
where id = #{id,jdbcType=INTEGER}

mapper.update:
update message
set comments_count=comments_count+#{count}
where id =#{id}
